define([],(function(){return{toggleCheckboxes:function(e){var c=document.querySelectorAll('input[type="checkbox"]'),r=Array.from(c).every((e=>e.checked));c.forEach((function(e){e.checked=!r}));var t=Array.from(c).every((e=>e.checked));e.textContent=t?"Deselect all":"Select all"}}}));